<?php include 'kon/koneksi.php';if (trim($_POST['assign_loc_id']) ==''){$error[]= '- assign_loc_id harus diisi';}if (trim($_POST['lampu_merah_id']) ==''){$error[]= '- lampu_merah_id harus diisi';}if (trim($_POST['icon']) ==''){$error[]= '- icon harus diisi';}if (trim($_POST['keterangan']) ==''){$error[]= '- keterangan harus diisi';}$assign_loc_id=$_POST['assign_loc_id'];$lampu_merah_id=$_POST['lampu_merah_id'];$icon=$_POST['icon'];$keterangan=$_POST['keterangan'];if (isset($error)){echo implode("<br />", $error);}else{try{$pdo = new PDOx();$dbh=$pdo->getKoneksi();$dbh->setAttribute( PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION );$sql="INSERT INTO tabel_assign_lampu_merah(assign_loc_id,lampu_merah_id,icon,keterangan)VALUES (:assign_loc_id,:lampu_merah_id,:icon,:keterangan)"; $q = $dbh->prepare($sql); $q->execute(array('assign_loc_id'=>$assign_loc_id,'lampu_merah_id'=>$lampu_merah_id,'icon'=>$icon,'keterangan'=>$keterangan)); echo "Succes add data!";} catch(PDOException $e){echo "Error". $e->getMessage(); exit;}}?>