<?php include 'kon/koneksi.php';if (trim($_POST['id']) ==''){$error[]= '- id harus diisi';}if (trim($_POST['jalur_id']) ==''){$error[]= '- jalur_id harus diisi';}if (trim($_POST['moda_id']) ==''){$error[]= '- moda_id harus diisi';}$id=$_POST['id'];$jalur_id=$_POST['jalur_id'];$moda_id=$_POST['moda_id'];if (isset($error)){echo implode("<br />", $error);}else{try{$pdo = new PDOx();$dbh=$pdo->getKoneksi();$dbh->setAttribute( PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION );$sql="INSERT INTO tabel_assign_moda(id,jalur_id,moda_id)VALUES (:id,:jalur_id,:moda_id)"; $q = $dbh->prepare($sql); $q->execute(array('id'=>$id,'jalur_id'=>$jalur_id,'moda_id'=>$moda_id)); echo "Succes add data!";} catch(PDOException $e){echo "Error". $e->getMessage(); exit;}}?>