<?php include 'kon/koneksi.php';if (trim($_POST['id']) ==''){$error[]= '- id harus diisi';}if (trim($_POST['nama']) ==''){$error[]= '- nama harus diisi';}if (trim($_POST['keterangan']) ==''){$error[]= '- keterangan harus diisi';}$id=$_POST['id'];$nama=$_POST['nama'];$keterangan=$_POST['keterangan'];if (isset($error)){echo implode("<br />", $error);}else{try{$pdo = new PDOx();$dbh=$pdo->getKoneksi();$dbh->setAttribute( PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION );$sql="INSERT INTO tabel_jalur(id,nama,keterangan)VALUES (:id,:nama,:keterangan)"; $q = $dbh->prepare($sql); $q->execute(array('id'=>$id,'nama'=>$nama,'keterangan'=>$keterangan)); echo "Succes add data!";} catch(PDOException $e){echo "Error". $e->getMessage(); exit;}}?>