<?php include 'kon/koneksi.php';if (trim($_POST['id']) ==''){$error[]= '- id harus diisi';}if (trim($_POST['lokasi']) ==''){$error[]= '- lokasi harus diisi';}if (trim($_POST['photo']) ==''){$error[]= '- photo harus diisi';}$id=$_POST['id'];$lokasi=$_POST['lokasi'];$photo=$_POST['photo'];if (isset($error)){echo implode("<br />", $error);}else{try{$pdo = new PDOx();$dbh=$pdo->getKoneksi();$dbh->setAttribute( PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION );$sql="INSERT INTO tabel_lampu_merah(id,lokasi,photo)VALUES (:id,:lokasi,:photo)"; $q = $dbh->prepare($sql); $q->execute(array('id'=>$id,'lokasi'=>$lokasi,'photo'=>$photo)); echo "Succes add data!";} catch(PDOException $e){echo "Error". $e->getMessage(); exit;}}?>